// Smooth scrolling
$(function(){$(window).scroll(function(){$(window).scrollTop()!=0?($("#nav").css("background-color","rgba(245,245,245,1)"),$("#nav").css("border-bottom","1px solid #ccc")):($("#nav").css("background-color","rgba(255,255,255,0.5)"),$("#nav").css("border-bottom","none"))}),$("#nav a, #news a").bind("click",function(e){var t=$(this);return $("html, body").stop().animate({scrollTop:$(t.attr("href")).offset().top-$("#nav").height()-24},"normal"),!1}),$(".back_top").bind("click",function(e){return $("html, body").animate({scrollTop:0},"normal"),!1})}),$(function(){$("#sponsors a.brochure").bind("click",function(e){_gaq.push(["_trackEvent","Sponsors","Brochure Download"])})});